import React, { useState, useRef, useEffect } from "react";
import html2canvas from "html2canvas";
import { Rnd } from "react-rnd";
import { FaTrash, FaUndo, FaRedo, FaClone, FaLayerGroup, FaExpand } from "react-icons/fa";
import clay from "./images/window-clay.png";
import white from "./images/window-white.png";
import black from "./images/window-black.png";
import brown from "./images/window-brown.png";

const windowImages = [
  { src: white, label: "White" },
  { src: black, label: "Black" },
  { src: brown, label: "Brown" },
  { src: clay, label: "Clay" },
];

export default function Visualizer() {
  const [background, setBackground] = useState(null);
  const [elements, setElements] = useState([]);
  const [history, setHistory] = useState([]);
  const [future, setFuture] = useState([]);
  const [selectedId, setSelectedId] = useState(null);
  const containerRef = useRef(null);

  const addWindow = (src) => {
    const newElement = {
      id: Date.now(),
      src,
      x: 50,
      y: 50,
      width: 150,
      height: 150,
      z: elements.length
    };
    updateHistory([...elements, newElement]);
    setSelectedId(newElement.id);
  };

  const updateHistory = (newElements) => {
    setHistory([...history, elements]);
    setElements(newElements);
    setFuture([]);
  };

  const handleBackgroundChange = (e) => {
    const reader = new FileReader();
    reader.onload = () => setBackground(reader.result);
    reader.readAsDataURL(e.target.files[0]);
  };

  const handleExport = () => {
    if (!containerRef.current) return;
    html2canvas(containerRef.current).then((canvas) => {
      const link = document.createElement("a");
      link.download = "visualization.png";
      link.href = canvas.toDataURL();
      link.click();
    });
  };

const handleAutoDetect = async () => {
  if (!background) return alert("Upload a background image first.");

  try {
    // Convert base64 background to Blob
    const blob = await fetch(background).then(res => res.blob());

    const formData = new FormData();
    formData.append("image", blob, "background.png");

    const response = await fetch("http://localhost:5000/highlight-windows", {
      method: "POST",
      body: formData
    });

    const result = await response.json();

    if (result.processed_image) {
      setBackground(`data:image/png;base64,${result.processed_image}`);
    } else {
      alert("Failed to process image");
    }
  } catch (err) {
    console.error("Auto detect error:", err);
    alert("Error contacting backend");
  }
};







  const handleUndo = () => {
    if (history.length === 0) return;
    const prev = history[history.length - 1];
    setFuture([elements, ...future]);
    setElements(prev);
    setHistory(history.slice(0, -1));
  };

  const handleRedo = () => {
    if (future.length === 0) return;
    const next = future[0];
    setHistory([...history, elements]);
    setElements(next);
    setFuture(future.slice(1));
  };

  const bringForward = (id) => {
    const updated = elements.map((el) =>
      el.id === id ? { ...el, z: el.z + 1 } : el
    );
    updateHistory(updated);
  };

  const duplicateElement = (id) => {
    const element = elements.find((el) => el.id === id);
    if (!element) return;
    const copy = { ...element, id: Date.now(), x: element.x + 20, y: element.y + 20 };
    updateHistory([...elements, copy]);
  };

  const updateElement = (id, newProps) => {
    const updated = elements.map((el) =>
      el.id === id ? { ...el, ...newProps } : el
    );
    setElements(updated);
  };

  const removeElement = (id) => {
    updateHistory(elements.filter((el) => el.id !== id));
    if (selectedId === id) setSelectedId(null);
  };

  const handleFullScreen = () => {
    if (containerRef.current.requestFullscreen) {
      containerRef.current.requestFullscreen();
    }
  };

  useEffect(() => {
    localStorage.setItem("savedDesign", JSON.stringify(elements));
  }, [elements]);

  useEffect(() => {
    const saved = localStorage.getItem("savedDesign");
    if (saved) {
      setElements(JSON.parse(saved));
    }
  }, []);

  return (
    <div className="p-4" onClick={() => setSelectedId(null)}>
      <div className="flex flex-wrap gap-3 mb-4">

<button
  onClick={handleAutoDetect}
  className="bg-indigo-600 text-white px-4 py-2 rounded hover:bg-indigo-700"
>
  ðŸªŸ Auto Detect Windows
</button>







        <input type="file" onChange={handleBackgroundChange} accept="image/*" className="border rounded p-2" />
        {windowImages.map((img) => (
          <button
            key={img.label}
            onClick={(e) => {
              e.stopPropagation();
              addWindow(img.src);
            }}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add {img.label}
          </button>

        ))}
        <button onClick={(e) => { e.stopPropagation(); handleUndo(); }} className="px-3 py-2 bg-yellow-400 text-white rounded"><FaUndo /></button>
        <button onClick={(e) => { e.stopPropagation(); handleRedo(); }} className="px-3 py-2 bg-yellow-400 text-white rounded"><FaRedo /></button>
        <button onClick={(e) => { e.stopPropagation(); handleExport(); }} className="px-3 py-2 bg-green-600 text-white rounded">Export Image</button>
        <button onClick={(e) => { e.stopPropagation(); handleFullScreen(); }} className="px-3 py-2 bg-purple-600 text-white rounded"><FaExpand /></button>
      </div>

      <div
        ref={containerRef}
        className="relative border-2 border-gray-300 w-full h-[80vh] bg-center bg-cover overflow-hidden"
        style={{ backgroundImage: `url(${background})` }}
      >
        {elements.map((el) => (
          <Rnd
            key={el.id}
            size={{ width: el.width, height: el.height }}
            position={{ x: el.x, y: el.y }}
            onDragStop={(e, d) => updateElement(el.id, { x: d.x, y: d.y })}
            onResizeStop={(e, dir, ref, delta, position) =>
              updateElement(el.id, {
                width: parseInt(ref.style.width),
                height: parseInt(ref.style.height),
                ...position,
              })
            }
            style={{ zIndex: el.z }}
            bounds="parent"
            className={`absolute transition-all duration-150 ${selectedId === el.id ? "border-2 border-blue-500" : "border-none"}`}
            onClick={(e) => {
              e.stopPropagation();
              setSelectedId(el.id);
            }}
          >
            <div className="w-full h-full relative">
              <img src={el.src} alt="window" className="w-full h-full object-contain" />
              {selectedId === el.id && (
                <div className="absolute top-1 right-1 flex gap-1">
                  <button onClick={(e) => { e.stopPropagation(); removeElement(el.id); }}><FaTrash className="text-red-600" /></button>
                  <button onClick={(e) => { e.stopPropagation(); duplicateElement(el.id); }}><FaClone className="text-green-600" /></button>
                  <button onClick={(e) => { e.stopPropagation(); bringForward(el.id); }}><FaLayerGroup className="text-blue-600" /></button>
                </div>
              )}
            </div>
          </Rnd>
        ))}
      </div>
    </div>
  );
}
