import React, { useState, useRef } from "react";

export default function Visualizer() {
  const [houseImage, setHouseImage] = useState(null);
  const [windows, setWindows] = useState([]);
  const canvasRef = useRef(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    const url = URL.createObjectURL(file);
    setHouseImage(url);
  };

  const addWindow = () => {
    setWindows([
      ...windows,
      {
        id: Date.now(),
        x: 100,
        y: 100,
        width: 120,
        height: 160,
        color: "white",
        image: "/double-hung-white.png",
      },
    ]);
  };

  const updateWindow = (id, updates) => {
    setWindows((prev) =>
      prev.map((w) => (w.id === id ? { ...w, ...updates } : w))
    );
  };

  const removeWindow = (id) => {
    setWindows((prev) => prev.filter((w) => w.id !== id));
  };

  const handleMouseDown = (e, id) => {
    const startX = e.clientX;
    const startY = e.clientY;

    const selected = windows.find((w) => w.id === id);
    const offsetX = startX - selected.x;
    const offsetY = startY - selected.y;

    const onMouseMove = (moveEvent) => {
      updateWindow(id, {
        x: moveEvent.clientX - offsetX,
        y: moveEvent.clientY - offsetY,
      });
    };

    const onMouseUp = () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };

    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
  };

  return (
    <div className="p-6 min-h-screen bg-gray-100">
      <h2 className="text-2xl font-bold mb-4">Window Visualizer</h2>

      <div className="mb-4 space-x-4">
        <input type="file" accept="image/*" onChange={handleImageUpload} />
        <button onClick={addWindow} className="bg-blue-600 text-white px-4 py-2 rounded">
          ➕ Add Window
        </button>
      </div>

      <div
        ref={canvasRef}
        className="relative border-2 border-dashed border-gray-300 bg-white overflow-hidden mx-auto"
        style={{ maxWidth: "100%", height: "auto", minHeight: 400 }}
      >
        {houseImage && (
          <img
            src={houseImage}
            alt="House"
            className="w-full h-auto object-contain"
          />
        )}

        {windows.map((w) => (
          <div
            key={w.id}
            onMouseDown={(e) => handleMouseDown(e, w.id)}
            style={{
              position: "absolute",
              top: w.y,
              left: w.x,
              width: w.width,
              height: w.height,
              cursor: "move",
            }}
          >
            <img
              src={w.image}
              alt="Window"
              style={{ width: "100%", height: "100%", objectFit: "contain" }}
            />
            <div className="absolute bottom-0 left-0 right-0 flex justify-center space-x-2 bg-white/80 p-1 text-xs">
              <select
                value={w.color}
                onChange={(e) =>
                  updateWindow(w.id, {
                    color: e.target.value,
                    image: `/double-hung-${e.target.value}.png`,
                  })
                }
              >
                <option value="white">White</option>
                <option value="black">Black</option>
                <option value="brown">Brown</option>
                <option value="clay">Clay</option>
              </select>
              <button
                className="text-red-600"
                onClick={() => removeWindow(w.id)}
              >
                ✖
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
